import React from 'react';

const Logo = ({ 
  width = "120", 
  height = "40", 
  className = "",
  showText = true,
  variant = "default" // "default", "white", "dark"
}) => {
  const getColors = () => {
    switch (variant) {
      case 'white':
        return {
          gradient: 'url(#gradient-white)',
          text: '#333333',
          icon: '#667eea'
        };
      case 'dark':
        return {
          gradient: 'url(#gradient-dark)',
          text: '#ffffff',
          icon: '#ffffff'
        };
      default:
        return {
          gradient: 'url(#gradient-default)',
          text: '#ffffff',
          icon: '#ffffff'
        };
    }
  };

  const colors = getColors();

  return (
    <svg 
      width={width} 
      height={height} 
      viewBox="0 0 120 40" 
      fill="none" 
      xmlns="http://www.w3.org/2000/svg"
      className={className}
    >
      {/* Background */}
      <rect width="120" height="40" rx="8" fill={colors.gradient}/>
      
      {/* Art Brush Icon */}
      <g transform="translate(8, 8)">
        <path d="M4 2L2 4L8 10L10 8L4 2Z" fill={colors.icon} opacity="0.9"/>
        <path d="M8 10L6 12L8 14L10 12L8 10Z" fill={colors.icon} opacity="0.7"/>
        <circle cx="11" cy="13" r="2" fill={colors.icon} opacity="0.8"/>
      </g>
      
      {/* Text */}
      {showText && (
        <>
          <text x="28" y="16" fontFamily="Arial, sans-serif" fontSize="11" fontWeight="bold" fill={colors.text}>
            Artisans
          </text>
          <text x="28" y="28" fontFamily="Arial, sans-serif" fontSize="11" fontWeight="bold" fill={colors.text}>
            Hub
          </text>
        </>
      )}
      
      {/* Gradient Definitions */}
      <defs>
        <linearGradient id="gradient-default" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style={{stopColor:'#667eea', stopOpacity:1}} />
          <stop offset="100%" style={{stopColor:'#764ba2', stopOpacity:1}} />
        </linearGradient>
        <linearGradient id="gradient-white" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style={{stopColor:'#ffffff', stopOpacity:1}} />
          <stop offset="100%" style={{stopColor:'#f8f9fa', stopOpacity:1}} />
        </linearGradient>
        <linearGradient id="gradient-dark" x1="0%" y1="0%" x2="100%" y2="100%">
          <stop offset="0%" style={{stopColor:'#2d3748', stopOpacity:1}} />
          <stop offset="100%" style={{stopColor:'#1a202c', stopOpacity:1}} />
        </linearGradient>
      </defs>
    </svg>
  );
};

export default Logo;
