import React from 'react';

const Logo = ({ 
  width = "120", 
  height = "40", 
  className = "",
  variant = "default" // "default", "white", "dark"
}) => {
  const getStyles = () => {
    const baseStyle = {
      width: width + 'px',
      height: height + 'px',
      objectFit: 'contain'
    };

    switch (variant) {
      case 'white':
        return {
          ...baseStyle,
          filter: 'brightness(0) saturate(100%) invert(27%) sepia(51%) saturate(2878%) hue-rotate(346deg) brightness(104%) contrast(97%)'
        };
      case 'dark':
        return {
          ...baseStyle,
          filter: 'brightness(0) saturate(100%) invert(0%) sepia(0%) saturate(0%) hue-rotate(0deg) brightness(100%) contrast(100%)'
        };
      default:
        return baseStyle;
    }
  };

  return (
    <img 
      src="/logo.png" 
      alt="Artisans Hub Logo" 
      style={getStyles()}
      className={`logo ${className}`}
      onError={(e) => {
        console.warn('Logo image failed to load, using fallback');
        e.target.style.display = 'none';
        if (e.target.nextSibling) {
          e.target.nextSibling.style.display = 'inline';
        }
      }}
    />
  );
};

export default Logo;
