import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Container, 
  Typography, 
  Button, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia,
  Chip,
  IconButton,
  TextField,
  InputAdornment,
  Paper,
  Avatar,
  Rating,
  LinearProgress
} from '@mui/material';
import {
  Search as SearchIcon,
  CameraAlt as CameraIcon,
  Mic as MicIcon,
  TrendingUp as TrendingIcon,
  Star as StarIcon,
  Favorite as FavoriteIcon,
  Share as ShareIcon,
  ViewInAr as ArIcon,
  Psychology as AIIcon,
  EcoFriendly as EcoIcon,
  VerifiedUser as VerifiedIcon,
  Language as TranslateIcon
} from '@mui/icons-material';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

// Import custom hooks and components
import { useAuth } from '../hooks/useAuth';
import { useNotifications } from '../hooks/useNotifications';
import ImageSearchModal from '../components/ai/ImageSearchModal';
import VoiceSearchModal from '../components/ai/VoiceSearchModal';
import RecommendationCarousel from '../components/product/RecommendationCarousel';
import ArtisanSpotlight from '../components/artisan/ArtisanSpotlight';
import CulturalHeritage from '../components/common/CulturalHeritage';
import SustainabilityMetrics from '../components/common/SustainabilityMetrics';

const HomePage = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { showNotification } = useNotifications();

  // State management
  const [searchTerm, setSearchTerm] = useState('');
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [trendingCategories, setTrendingCategories] = useState([]);
  const [artisanSpotlights, setArtisanSpotlights] = useState([]);
  const [loading, setLoading] = useState(true);
  const [imageSearchOpen, setImageSearchOpen] = useState(false);
  const [voiceSearchOpen, setVoiceSearchOpen] = useState(false);
  const [sustainabilityData, setSustainabilityData] = useState(null);
  const [culturalInsights, setCulturalInsights] = useState([]);

  // Load data on component mount
  useEffect(() => {
    loadHomePageData();
  }, [user]);

  const loadHomePageData = async () => {
    try {
      setLoading(true);

      // Parallel API calls for better performance
      const [
        productsResponse,
        categoriesResponse,
        artisansResponse,
        sustainabilityResponse,
        culturalResponse
      ] = await Promise.all([
        fetch('/api/products?limit=12&sort_by=likes'),
        fetch('/api/categories/trending'),
        fetch('/api/artisans/spotlight'),
        fetch('/api/sustainability/metrics'),
        fetch('/api/cultural-heritage/insights')
      ]);

      // Process responses
      if (productsResponse.ok) {
        const data = await productsResponse.json();
        setFeaturedProducts(data.products || []);
      }

      if (categoriesResponse.ok) {
        const data = await categoriesResponse.json();
        setTrendingCategories(data.categories || defaultCategories);
      } else {
        setTrendingCategories(defaultCategories);
      }

      if (artisansResponse.ok) {
        const data = await artisansResponse.json();
        setArtisanSpotlights(data.artisans || []);
      }

      if (sustainabilityResponse.ok) {
        const data = await sustainabilityResponse.json();
        setSustainabilityData(data);
      }

      if (culturalResponse.ok) {
        const data = await culturalResponse.json();
        setCulturalInsights(data.insights || []);
      }

    } catch (error) {
      console.error('Error loading homepage data:', error);
      showNotification('Failed to load some content. Please refresh the page.', 'error');
      // Set fallback data
      setTrendingCategories(defaultCategories);
    } finally {
      setLoading(false);
    }
  };

  // Default categories for fallback
  const defaultCategories = [
    { name: 'Pottery', icon: '🏺', color: '#8B4513', description: 'Handcrafted ceramics and clay art' },
    { name: 'Wooden Dolls', icon: '🪆', color: '#D2691E', description: 'Traditional carved figurines' },
    { name: 'Basket Weaving', icon: '🧺', color: '#DAA520', description: 'Eco-friendly woven storage' },
    { name: 'Handlooms', icon: '🧵', color: '#CD853F', description: 'Heritage textiles and fabrics' }
  ];

  // Handle search functionality
  const handleSearch = (term = searchTerm) => {
    if (!term.trim()) {
      showNotification('Please enter a search term', 'warning');
      return;
    }
    navigate(`/products?search=${encodeURIComponent(term)}`);
  };

  const handleImageSearch = (imageData) => {
    // Process image search through AI
    setImageSearchOpen(false);
    navigate('/ai-assistant', { state: { imageSearch: imageData } });
  };

  const handleVoiceSearch = (transcript) => {
    setVoiceSearchOpen(false);
    setSearchTerm(transcript);
    handleSearch(transcript);
  };

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        duration: 0.6,
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: { duration: 0.5 }
    }
  };

  return (
    <motion.div
      variants={containerVariants}
      initial="hidden"
      animate="visible"
    >
      {/* Hero Section */}
      <Box
        sx={{
          background: 'linear-gradient(135deg, #8B4513 0%, #D2691E 50%, #DAA520 100%)',
          color: 'white',
          py: { xs: 8, md: 12 },
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        {/* Background Pattern */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundImage: 'url("data:image/svg+xml,%3Csvg width="40" height="40" viewBox="0 0 40 40" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="%23ffffff" fill-opacity="0.1"%3E%3Cpath d="M20 20c0 11.046-8.954 20-20 20v20h40V20H20z"/%3E%3C/g%3E%3C/svg%3E")',
            opacity: 0.1
          }}
        />
        
        <Container maxWidth="lg" sx={{ position: 'relative', zIndex: 2 }}>
          <motion.div variants={itemVariants}>
            <Grid container spacing={4} alignItems="center">
              <Grid item xs={12} md={6}>
                <Typography
                  variant="h1"
                  sx={{
                    fontSize: { xs: '2.5rem', md: '4rem' },
                    fontWeight: 700,
                    mb: 3,
                    background: 'linear-gradient(45deg, #FFFFFF 30%, #DAA520 90%)',
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                  }}
                >
                  AI-Powered Artisan Marketplace
                </Typography>
                
                <Typography
                  variant="h5"
                  sx={{ mb: 4, opacity: 0.95, lineHeight: 1.6 }}
                >
                  Discover authentic handicrafts with intelligent recommendations, 
                  AR try-ons, and stories that preserve cultural heritage.
                </Typography>

                {/* Smart Search Bar */}
                <Box sx={{ mb: 4 }}>
                  <Paper
                    elevation={8}
                    sx={{
                      p: 1,
                      display: 'flex',
                      alignItems: 'center',
                      borderRadius: 3,
                      background: 'rgba(255, 255, 255, 0.95)',
                      backdropFilter: 'blur(10px)'
                    }}
                  >
                    <TextField
                      fullWidth
                      placeholder="Search for authentic handicrafts..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                      InputProps={{
                        startAdornment: (
                          <InputAdornment position="start">
                            <SearchIcon color="action" />
                          </InputAdornment>
                        ),
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton
                              onClick={() => setImageSearchOpen(true)}
                              title="Search by Image"
                              color="primary"
                            >
                              <CameraIcon />
                            </IconButton>
                            <IconButton
                              onClick={() => setVoiceSearchOpen(true)}
                              title="Voice Search"
                              color="primary"
                            >
                              <MicIcon />
                            </IconButton>
                          </InputAdornment>
                        )
                      }}
                      sx={{
                        '& .MuiOutlinedInput-root': {
                          border: 'none',
                          '& fieldset': { border: 'none' }
                        }
                      }}
                    />
                    <Button
                      variant="contained"
                      onClick={() => handleSearch()}
                      sx={{
                        ml: 1,
                        px: 3,
                        py: 1.5,
                        borderRadius: 2,
                        boxShadow: 'none'
                      }}
                    >
                      Search
                    </Button>
                  </Paper>
                </Box>

                {/* CTA Buttons */}
                <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                  <Button
                    variant="contained"
                    size="large"
                    startIcon={<AIIcon />}
                    onClick={() => navigate('/ai-assistant')}
                    sx={{
                      bgcolor: 'rgba(255, 255, 255, 0.2)',
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(255, 255, 255, 0.3)',
                      '&:hover': {
                        bgcolor: 'rgba(255, 255, 255, 0.3)',
                      }
                    }}
                  >
                    AI Assistant
                  </Button>
                  <Button
                    variant="outlined"
                    size="large"
                    startIcon={<ArIcon />}
                    onClick={() => navigate('/products?ar=true')}
                    sx={{
                      borderColor: 'rgba(255, 255, 255, 0.5)',
                      color: 'white',
                      '&:hover': {
                        borderColor: 'white',
                        bgcolor: 'rgba(255, 255, 255, 0.1)'
                      }
                    }}
                  >
                    AR Try-On
                  </Button>
                </Box>
              </Grid>

              {/* Hero Visual */}
              <Grid item xs={12} md={6}>
                <motion.div
                  animate={{
                    y: [0, -10, 0],
                  }}
                  transition={{
                    duration: 4,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  <Box
                    component="img"
                    src="/images/hero-artisan-collage.jpg"
                    alt="Artisan crafts collage"
                    sx={{
                      width: '100%',
                      height: 'auto',
                      borderRadius: 3,
                      boxShadow: '0 20px 40px rgba(0,0,0,0.3)',
                      filter: 'drop-shadow(0 0 20px rgba(255,255,255,0.2))'
                    }}
                    onError={(e) => {
                      // Fallback if image not found
                      e.target.style.display = 'none';
                    }}
                  />
                </motion.div>
              </Grid>
            </Grid>
          </motion.div>
        </Container>
      </Box>

      {/* Trending Categories */}
      <Container maxWidth="lg" sx={{ py: 8 }}>
        <motion.div variants={itemVariants}>
          <Typography
            variant="h3"
            textAlign="center"
            sx={{ mb: 6, fontWeight: 600 }}
          >
            Explore Authentic Crafts
          </Typography>

          <Grid container spacing={3}>
            {trendingCategories.map((category, index) => (
              <Grid item xs={12} sm={6} md={3} key={category.name}>
                <motion.div
                  variants={itemVariants}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  <Card
                    sx={{
                      height: '100%',
                      cursor: 'pointer',
                      background: `linear-gradient(135deg, ${category.color}15 0%, ${category.color}30 100%)`,
                      border: `2px solid ${category.color}40`,
                      transition: 'all 0.3s ease'
                    }}
                    onClick={() => navigate(`/products?category=${category.name}`)}
                  >
                    <CardContent sx={{ textAlign: 'center', p: 3 }}>
                      <Typography variant="h2" sx={{ fontSize: '3rem', mb: 2 }}>
                        {category.icon}
                      </Typography>
                      <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
                        {category.name}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {category.description}
                      </Typography>
                    </CardContent>
                  </Card>
                </motion.div>
              </Grid>
            ))}
          </Grid>
        </motion.div>
      </Container>

      {/* AI-Powered Features Section */}
      <Box sx={{ bgcolor: 'background.paper', py: 8 }}>
        <Container maxWidth="lg">
          <motion.div variants={itemVariants}>
            <Typography
              variant="h3"
              textAlign="center"
              sx={{ mb: 2, fontWeight: 600 }}
            >
              Powered by Intelligence
            </Typography>
            <Typography
              variant="h6"
              textAlign="center"
              color="text.secondary"
              sx={{ mb: 6 }}
            >
              Experience the future of artisan marketplaces with AI-driven features
            </Typography>

            <Grid container spacing={4}>
              {[
                {
                  icon: <AIIcon sx={{ fontSize: 40 }} />,
                  title: 'Smart Classification',
                  description: 'Our AI accurately identifies and categorizes handicrafts, ensuring authentic listings.',
                  color: '#1976d2'
                },
                {
                  icon: <ArIcon sx={{ fontSize: 40 }} />,
                  title: 'AR Try-Before-Buy',
                  description: 'Visualize products in your space with augmented reality technology.',
                  color: '#388e3c'
                },
                {
                  icon: <TranslateIcon sx={{ fontSize: 40 }} />,
                  title: 'Multi-Language Support',
                  description: 'Break language barriers with real-time translation and voice navigation.',
                  color: '#f57c00'
                },
                {
                  icon: <EcoIcon sx={{ fontSize: 40 }} />,
                  title: 'Sustainability Tracking',
                  description: 'Monitor environmental impact and support eco-friendly artisan practices.',
                  color: '#00796b'
                }
              ].map((feature, index) => (
                <Grid item xs={12} sm={6} md={3} key={index}>
                  <motion.div
                    variants={itemVariants}
                    whileHover={{ y: -5 }}
                  >
                    <Card sx={{ height: '100%', textAlign: 'center', p: 3 }}>
                      <Box sx={{ color: feature.color, mb: 2 }}>
                        {feature.icon}
                      </Box>
                      <Typography variant="h6" sx={{ fontWeight: 600, mb: 2 }}>
                        {feature.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {feature.description}
                      </Typography>
                    </Card>
                  </motion.div>
                </Grid>
              ))}
            </Grid>
          </motion.div>
        </Container>
      </Box>

      {/* Featured Products */}
      {!loading && featuredProducts.length > 0 && (
        <Container maxWidth="lg" sx={{ py: 8 }}>
          <motion.div variants={itemVariants}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
              <TrendingIcon sx={{ mr: 2, color: 'primary.main' }} />
              <Typography variant="h4" sx={{ fontWeight: 600 }}>
                Trending Now
              </Typography>
            </Box>

            <Grid container spacing={3}>
              {featuredProducts.slice(0, 8).map((product, index) => (
                <Grid item xs={12} sm={6} md={3} key={product.id}>
                  <ProductCard product={product} index={index} />
                </Grid>
              ))}
            </Grid>

            <Box sx={{ textAlign: 'center', mt: 4 }}>
              <Button
                variant="contained"
                size="large"
                onClick={() => navigate('/products')}
              >
                View All Products
              </Button>
            </Box>
          </motion.div>
        </Container>
      )}

      {/* Sustainability Metrics */}
      {sustainabilityData && (
        <SustainabilityMetrics data={sustainabilityData} />
      )}

      {/* Cultural Heritage Section */}
      {culturalInsights.length > 0 && (
        <CulturalHeritage insights={culturalInsights} />
      )}

      {/* Artisan Spotlights */}
      {artisanSpotlights.length > 0 && (
        <ArtisanSpotlight artisans={artisanSpotlights} />
      )}

      {/* Personalized Recommendations */}
      {user && (
        <RecommendationCarousel userId={user.id} />
      )}

      {/* Modals */}
      <ImageSearchModal
        open={imageSearchOpen}
        onClose={() => setImageSearchOpen(false)}
        onSearch={handleImageSearch}
      />

      <VoiceSearchModal
        open={voiceSearchOpen}
        onClose={() => setVoiceSearchOpen(false)}
        onSearch={handleVoiceSearch}
      />

      {/* Loading overlay */}
      {loading && (
        <Box
          sx={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            bgcolor: 'rgba(255, 255, 255, 0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999
          }}
        >
          <Box sx={{ textAlign: 'center' }}>
            <LinearProgress sx={{ mb: 2, width: 200 }} />
            <Typography>Loading amazing content...</Typography>
          </Box>
        </Box>
      )}
    </motion.div>
  );
};

// Product Card Component
const ProductCard = ({ product, index }) => {
  const navigate = useNavigate();
  const [liked, setLiked] = useState(false);

  return (
    <motion.div
      initial={{ opacity: 0, y: 50 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: index * 0.1 }}
    >
      <Card
        sx={{
          height: '100%',
          cursor: 'pointer',
          position: 'relative',
          '&:hover .product-actions': {
            opacity: 1,
            transform: 'translateY(0)'
          }
        }}
        onClick={() => navigate(`/products/${product.id}`)}
      >
        {/* Product Image */}
        <CardMedia
          component="img"
          height="200"
          image={product.images?.[0] ? `/uploads/${product.images[0]}` : '/images/placeholder-product.jpg'}
          alt={product.title}
          sx={{ objectFit: 'cover' }}
        />

        {/* AI Badge */}
        {product.ai_confidence > 0.8 && (
          <Chip
            icon={<VerifiedIcon />}
            label="AI Verified"
            size="small"
            color="success"
            sx={{
              position: 'absolute',
              top: 8,
              left: 8,
              fontWeight: 600
            }}
          />
        )}

        {/* Product Actions Overlay */}
        <Box
          className="product-actions"
          sx={{
            position: 'absolute',
            top: 8,
            right: 8,
            display: 'flex',
            flexDirection: 'column',
            gap: 1,
            opacity: 0,
            transform: 'translateY(-10px)',
            transition: 'all 0.3s ease'
          }}
        >
          <IconButton
            size="small"
            sx={{ bgcolor: 'background.paper' }}
            onClick={(e) => {
              e.stopPropagation();
              setLiked(!liked);
            }}
          >
            <FavoriteIcon color={liked ? 'error' : 'action'} />
          </IconButton>
          <IconButton
            size="small"
            sx={{ bgcolor: 'background.paper' }}
            onClick={(e) => {
              e.stopPropagation();
              // Handle share functionality
            }}
          >
            <ShareIcon />
          </IconButton>
          <IconButton
            size="small"
            sx={{ bgcolor: 'background.paper' }}
            onClick={(e) => {
              e.stopPropagation();
              navigate(`/ar-viewer/${product.id}`);
            }}
          >
            <ArIcon />
          </IconButton>
        </Box>

        <CardContent>
          <Typography
            variant="h6"
            noWrap
            sx={{ fontWeight: 600, mb: 1 }}
          >
            {product.title}
          </Typography>

          <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
            <Rating
              value={product.rating || 4.5}
              precision={0.5}
              size="small"
              readOnly
            />
            <Typography variant="body2" color="text.secondary" sx={{ ml: 1 }}>
              ({product.reviews_count || 12})
            </Typography>
          </Box>

          <Typography
            variant="h6"
            color="primary"
            sx={{ fontWeight: 700 }}
          >
            ${product.price}
          </Typography>

          <Typography
            variant="body2"
            color="text.secondary"
            sx={{ mt: 1 }}
          >
            by {product.business_name || 'Artisan'}
          </Typography>

          {/* AI Tags */}
          {product.ai_tags && product.ai_tags.length > 0 && (
            <Box sx={{ mt: 1, display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
              {product.ai_tags.slice(0, 2).map((tag, index) => (
                <Chip
                  key={index}
                  label={tag}
                  size="small"
                  variant="outlined"
                  sx={{ fontSize: '0.7rem' }}
                />
              ))}
            </Box>
          )}
        </CardContent>
      </Card>
    </motion.div>
  );
};

export default HomePage;
